countAssignedInspectionsBetweenDates.queryString:
select 
count(*) as inspectionsCount

from MasterInspections where closed=:isClosed and (:municipality IS NULL OR inspectionDesign.municipalities.id=:municipality) and assignedTo=:assignedTo and (:startDate is null or dateAssigned >= :startDate) and (:endDate is null or dateAssigned <= :endDate)

countAssignedInspectionsLessThanDate.queryString:
select 
count(*) as inspectionsCount

from MasterInspections where closed=:isClosed and assignedTo=:assignedTo and dateAssigned<:requestedFor and (:municipality IS NULL OR inspectionDesign.municipalities.id=:municipality)
