import com.civicxpress.cx2.FormFee;
import com.civicxpress.cx2.service.FormFeeService;

/**
 * Controller object for domain model class FormFee.
 * @see FormFee
 */
@RestController("cx2.FormFeeController")
@Api(value = "FormFeeController", description = "Exposes APIs to work with FormFee resource.")
@RequestMapping("/cx2/FormFee")
public class FormFeeController {

    private static final Logger LOGGER = LoggerFactory.getLogger(FormFeeController.class);

    @Autowired
    @Qualifier("cx2.FormFeeService")
    private FormFeeService formFeeService;

    @ApiOperation(value = "Creates a new FormFee instance.")
    @RequestMapping(method = RequestMethod.POST)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public FormFee createFormFee(@RequestBody FormFee formFee) {
        LOGGER.debug("Create FormFee with information: {}", formFee);
        formFee = formFeeService.create(formFee);
        LOGGER.debug("Created FormFee with information: {}", formFee);
        return formFee;
    }

    @ApiOperation(value = "Returns the FormFee instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public FormFee getFormFee(@PathVariable("id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Getting FormFee with id: {}", id);
        FormFee foundFormFee = formFeeService.getById(id);
        LOGGER.debug("FormFee details with id: {}", foundFormFee);
        return foundFormFee;
    }

    @ApiOperation(value = "Updates the FormFee instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.PUT)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public FormFee editFormFee(@PathVariable("id") Integer id, @RequestBody FormFee formFee) throws EntityNotFoundException {
        LOGGER.debug("Editing FormFee with id: {}", formFee.getId());
        formFee.setId(id);
        formFee = formFeeService.update(formFee);
        LOGGER.debug("FormFee details with id: {}", formFee);
        return formFee;
    }

    @ApiOperation(value = "Deletes the FormFee instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.DELETE)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public boolean deleteFormFee(@PathVariable("id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Deleting FormFee with id: {}", id);
        FormFee deletedFormFee = formFeeService.delete(id);
        return deletedFormFee != null;
    }

    /**
     * @deprecated Use {@link #findFormFees(String, Pageable)} instead.
     */
    @Deprecated
    @ApiOperation(value = "Returns the list of FormFee instances matching the search criteria.")
    @RequestMapping(value = "/search", method = RequestMethod.POST)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Page<FormFee> searchFormFeesByQueryFilters(Pageable pageable, @RequestBody QueryFilter[] queryFilters) {
        LOGGER.debug("Rendering FormFees list");
        return formFeeService.findAll(queryFilters, pageable);
    }

    @ApiOperation(value = "Returns the list of FormFee instances matching the search criteria.")
    @RequestMapping(method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Page<FormFee> findFormFees(@ApiParam("conditions to filter the results") @RequestParam(value = "q", required = false) String query, Pageable pageable) {
        LOGGER.debug("Rendering FormFees list");
        return formFeeService.findAll(query, pageable);
    }

    @ApiOperation(value = "Returns downloadable file for the data.")
    @RequestMapping(value = "/export/{exportType}", method = RequestMethod.GET, produces = "application/octet-stream")
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Downloadable exportFormFees(@PathVariable("exportType") ExportType exportType, @ApiParam("conditions to filter the results") @RequestParam(value = "q", required = false) String query, Pageable pageable) {
        return formFeeService.export(exportType, query, pageable);
    }

    @ApiOperation(value = "Returns the total count of FormFee instances.")
    @RequestMapping(value = "/count", method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Long countFormFees(@ApiParam("conditions to filter the results") @RequestParam(value = "q", required = false) String query) {
        LOGGER.debug("counting FormFees");
        return formFeeService.count(query);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service FormFeeService instance
	 */
    protected void setFormFeeService(FormFeeService service) {
        this.formFeeService = service;
    }
}
