import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;
import com.wavemaker.tools.api.core.annotations.WMAccessVisibility;
import com.wavemaker.tools.api.core.models.AccessSpecifier;
import com.wordnik.swagger.annotations.Api;
import com.wordnik.swagger.annotations.ApiOperation;
import com.wordnik.swagger.annotations.ApiParam;

import com.civicxpress.cx2.FormHistory;
import com.civicxpress.cx2.service.FormHistoryService;


/**
 * Controller object for domain model class FormHistory.
 * @see FormHistory
 */
@RestController("cx2.FormHistoryController")
@Api(value = "FormHistoryController", description = "Exposes APIs to work with FormHistory resource.")
@RequestMapping("/cx2/FormHistory")
public class FormHistoryController {

    private static final Logger LOGGER = LoggerFactory.getLogger(FormHistoryController.class);

    @Autowired
	@Qualifier("cx2.FormHistoryService")
	private FormHistoryService formHistoryService;

	@ApiOperation(value = "Creates a new FormHistory instance.")
	@RequestMapping(method = RequestMethod.POST)
        @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
	public FormHistory createFormHistory(@RequestBody FormHistory formHistory) {
		LOGGER.debug("Create FormHistory with information: {}" , formHistory);

		formHistory = formHistoryService.create(formHistory);
		LOGGER.debug("Created FormHistory with information: {}" , formHistory);

	    return formHistory;
	}


    @ApiOperation(value = "Returns the FormHistory instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public FormHistory getFormHistory(@PathVariable("id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Getting FormHistory with id: {}" , id);

        FormHistory foundFormHistory = formHistoryService.getById(id);
        LOGGER.debug("FormHistory details with id: {}" , foundFormHistory);

        return foundFormHistory;
    }

    @ApiOperation(value = "Updates the FormHistory instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.PUT)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public FormHistory editFormHistory(@PathVariable("id") Integer id, @RequestBody FormHistory formHistory) throws EntityNotFoundException {
        LOGGER.debug("Editing FormHistory with id: {}" , formHistory.getId());

        formHistory.setId(id);
        formHistory = formHistoryService.update(formHistory);
        LOGGER.debug("FormHistory details with id: {}" , formHistory);

        return formHistory;
    }

    @ApiOperation(value = "Deletes the FormHistory instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.DELETE)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public boolean deleteFormHistory(@PathVariable("id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Deleting FormHistory with id: {}" , id);

        FormHistory deletedFormHistory = formHistoryService.delete(id);

        return deletedFormHistory != null;
    }

    /**
     * @deprecated Use {@link #findFormHistories(String, Pageable)} instead.
     */
    @Deprecated
    @ApiOperation(value = "Returns the list of FormHistory instances matching the search criteria.")
    @RequestMapping(value = "/search", method = RequestMethod.POST)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Page<FormHistory> searchFormHistoriesByQueryFilters( Pageable pageable, @RequestBody QueryFilter[] queryFilters) {
        LOGGER.debug("Rendering FormHistories list");
        return formHistoryService.findAll(queryFilters, pageable);
    }

    @ApiOperation(value = "Returns the list of FormHistory instances matching the search criteria.")
    @RequestMapping(method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Page<FormHistory> findFormHistories(@ApiParam("conditions to filter the results") @RequestParam(value = "q", required = false) String query, Pageable pageable) {
        LOGGER.debug("Rendering FormHistories list");
        return formHistoryService.findAll(query, pageable);
    }

    @ApiOperation(value = "Returns downloadable file for the data.")
    @RequestMapping(value = "/export/{exportType}", method = RequestMethod.GET, produces = "application/octet-stream")
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public Downloadable exportFormHistories(@PathVariable("exportType") ExportType exportType, @ApiParam("conditions to filter the results") @RequestParam(value = "q", required = false) String query, Pageable pageable) {
         return formHistoryService.export(exportType, query, pageable);
    }

	@ApiOperation(value = "Returns the total count of FormHistory instances.")
	@RequestMapping(value = "/count", method = RequestMethod.GET)
	@WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
	public Long countFormHistories( @ApiParam("conditions to filter the results") @RequestParam(value = "q", required = false) String query) {
		LOGGER.debug("counting FormHistories");
		return formHistoryService.count(query);
	}


    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service FormHistoryService instance
	 */
	protected void setFormHistoryService(FormHistoryService service) {
		this.formHistoryService = service;
	}

}

