    @ApiOperation(value = "Creates a new GlobalSettings instance.")
    @RequestMapping(method = RequestMethod.POST)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public GlobalSettings createGlobalSettings(@RequestBody GlobalSettings globalSettings) {
        LOGGER.debug("Create GlobalSettings with information: {}", globalSettings);
        globalSettings = globalSettingsService.create(globalSettings);
        LOGGER.debug("Created GlobalSettings with information: {}", globalSettings);
        return globalSettings;
    }

    @ApiOperation(value = "Returns the GlobalSettings instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public GlobalSettings getGlobalSettings(@PathVariable("id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Getting GlobalSettings with id: {}", id);
        GlobalSettings foundGlobalSettings = globalSettingsService.getById(id);
        LOGGER.debug("GlobalSettings details with id: {}", foundGlobalSettings);
        return foundGlobalSettings;
    }

    @ApiOperation(value = "Updates the GlobalSettings instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.PUT)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public GlobalSettings editGlobalSettings(@PathVariable("id") Integer id, @RequestBody GlobalSettings globalSettings) throws EntityNotFoundException {
        LOGGER.debug("Editing GlobalSettings with id: {}", globalSettings.getId());
        globalSettings.setId(id);
        globalSettings = globalSettingsService.update(globalSettings);
        LOGGER.debug("GlobalSettings details with id: {}", globalSettings);
        return globalSettings;
    }

    @ApiOperation(value = "Deletes the GlobalSettings instance associated with the given id.")
    @RequestMapping(value = "/{id:.+}", method = RequestMethod.DELETE)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    public boolean deleteGlobalSettings(@PathVariable("id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Deleting GlobalSettings with id: {}", id);
        GlobalSettings deletedGlobalSettings = globalSettingsService.delete(id);
        return deletedGlobalSettings != null;
    }

