package com.civicxpress.cx2.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/


import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.CaseStatuses;
import com.civicxpress.cx2.MasterCases;


/**
 * ServiceImpl object for domain model class CaseStatuses.
 *
 * @see CaseStatuses
 */
@Service("cx2.CaseStatusesService")
public class CaseStatusesServiceImpl implements CaseStatusesService {

    private static final Logger LOGGER = LoggerFactory.getLogger(CaseStatusesServiceImpl.class);

    @Autowired
	@Qualifier("cx2.MasterCasesService")
	private MasterCasesService masterCasesService;

    @Autowired
    @Qualifier("cx2.CaseStatusesDao")
    private WMGenericDao<CaseStatuses, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<CaseStatuses, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public CaseStatuses create(CaseStatuses caseStatuses) {
        LOGGER.debug("Creating a new CaseStatuses with information: {}", caseStatuses);
        CaseStatuses caseStatusesCreated = this.wmGenericDao.create(caseStatuses);
        if(caseStatusesCreated.getMasterCaseses() != null) {
            for(MasterCases masterCasese : caseStatusesCreated.getMasterCaseses()) {
                masterCasese.setCaseStatuses(caseStatusesCreated);
                LOGGER.debug("Creating a new child MasterCases with information: {}", masterCasese);
                masterCasesService.create(masterCasese);
            }
        }
        return caseStatusesCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public CaseStatuses getById(Integer casestatusesId) throws EntityNotFoundException {
        LOGGER.debug("Finding CaseStatuses by id: {}", casestatusesId);
        CaseStatuses caseStatuses = this.wmGenericDao.findById(casestatusesId);
        if (caseStatuses == null){
            LOGGER.debug("No CaseStatuses found with id: {}", casestatusesId);
            throw new EntityNotFoundException(String.valueOf(casestatusesId));
        }
        return caseStatuses;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public CaseStatuses findById(Integer casestatusesId) {
        LOGGER.debug("Finding CaseStatuses by id: {}", casestatusesId);
        return this.wmGenericDao.findById(casestatusesId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public CaseStatuses update(CaseStatuses caseStatuses) throws EntityNotFoundException {
        LOGGER.debug("Updating CaseStatuses with information: {}", caseStatuses);
        this.wmGenericDao.update(caseStatuses);

        Integer casestatusesId = caseStatuses.getId();

        return this.wmGenericDao.findById(casestatusesId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public CaseStatuses delete(Integer casestatusesId) throws EntityNotFoundException {
        LOGGER.debug("Deleting CaseStatuses with id: {}", casestatusesId);
        CaseStatuses deleted = this.wmGenericDao.findById(casestatusesId);
        if (deleted == null) {
            LOGGER.debug("No CaseStatuses found with id: {}", casestatusesId);
            throw new EntityNotFoundException(String.valueOf(casestatusesId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<CaseStatuses> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all CaseStatuses");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<CaseStatuses> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all CaseStatuses");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table CaseStatuses to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<MasterCases> findAssociatedMasterCaseses(Integer id, Pageable pageable) {
        LOGGER.debug("Fetching all associated masterCaseses");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("caseStatuses.id = '" + id + "'");

        return masterCasesService.findAll(queryBuilder.toString(), pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service MasterCasesService instance
	 */
	protected void setMasterCasesService(MasterCasesService service) {
        this.masterCasesService = service;
    }

}

