	public FormCategories findById(Integer formcategoriesId) {
        LOGGER.debug("Finding FormCategories by id: {}", formcategoriesId);
        return this.wmGenericDao.findById(formcategoriesId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public FormCategories update(FormCategories formCategories) throws EntityNotFoundException {
        LOGGER.debug("Updating FormCategories with information: {}", formCategories);
        this.wmGenericDao.update(formCategories);

        Integer formcategoriesId = formCategories.getId();

        return this.wmGenericDao.findById(formcategoriesId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public FormCategories delete(Integer formcategoriesId) throws EntityNotFoundException {
        LOGGER.debug("Deleting FormCategories with id: {}", formcategoriesId);
        FormCategories deleted = this.wmGenericDao.findById(formcategoriesId);
        if (deleted == null) {
            LOGGER.debug("No FormCategories found with id: {}", formcategoriesId);
            throw new EntityNotFoundException(String.valueOf(formcategoriesId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

