import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.data.model.AggregationInfo;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.GisTransaction;


/**
 * ServiceImpl object for domain model class GisTransaction.
 *
 * @see GisTransaction
 */
@Service("cx2.GisTransactionService")
public class GisTransactionServiceImpl implements GisTransactionService {

    private static final Logger LOGGER = LoggerFactory.getLogger(GisTransactionServiceImpl.class);


    @Autowired
    @Qualifier("cx2.GisTransactionDao")
    private WMGenericDao<GisTransaction, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<GisTransaction, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public GisTransaction create(GisTransaction gisTransaction) {
        LOGGER.debug("Creating a new GisTransaction with information: {}", gisTransaction);
        GisTransaction gisTransactionCreated = this.wmGenericDao.create(gisTransaction);
        return gisTransactionCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public GisTransaction getById(Integer gistransactionId) throws EntityNotFoundException {
        LOGGER.debug("Finding GisTransaction by id: {}", gistransactionId);
        GisTransaction gisTransaction = this.wmGenericDao.findById(gistransactionId);
        if (gisTransaction == null){
            LOGGER.debug("No GisTransaction found with id: {}", gistransactionId);
            throw new EntityNotFoundException(String.valueOf(gistransactionId));
        }
        return gisTransaction;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public GisTransaction findById(Integer gistransactionId) {
        LOGGER.debug("Finding GisTransaction by id: {}", gistransactionId);
        return this.wmGenericDao.findById(gistransactionId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public GisTransaction update(GisTransaction gisTransaction) throws EntityNotFoundException {
        LOGGER.debug("Updating GisTransaction with information: {}", gisTransaction);
        this.wmGenericDao.update(gisTransaction);

        Integer gistransactionId = gisTransaction.getId();

        return this.wmGenericDao.findById(gistransactionId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public GisTransaction delete(Integer gistransactionId) throws EntityNotFoundException {
        LOGGER.debug("Deleting GisTransaction with id: {}", gistransactionId);
        GisTransaction deleted = this.wmGenericDao.findById(gistransactionId);
        if (deleted == null) {
            LOGGER.debug("No GisTransaction found with id: {}", gistransactionId);
            throw new EntityNotFoundException(String.valueOf(gistransactionId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<GisTransaction> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all GisTransactions");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<GisTransaction> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all GisTransactions");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table GisTransaction to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}

