package com.civicxpress.cx2.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/


import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.Inspections;


/**
 * ServiceImpl object for domain model class Inspections.
 *
 * @see Inspections
 */
@Service("cx2.InspectionsService")
public class InspectionsServiceImpl implements InspectionsService {

    private static final Logger LOGGER = LoggerFactory.getLogger(InspectionsServiceImpl.class);


    @Autowired
    @Qualifier("cx2.InspectionsDao")
    private WMGenericDao<Inspections, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Inspections, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public Inspections create(Inspections inspections) {
        LOGGER.debug("Creating a new Inspections with information: {}", inspections);
        Inspections inspectionsCreated = this.wmGenericDao.create(inspections);
        return inspectionsCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Inspections getById(Integer inspectionsId) throws EntityNotFoundException {
        LOGGER.debug("Finding Inspections by id: {}", inspectionsId);
        Inspections inspections = this.wmGenericDao.findById(inspectionsId);
        if (inspections == null){
            LOGGER.debug("No Inspections found with id: {}", inspectionsId);
            throw new EntityNotFoundException(String.valueOf(inspectionsId));
        }
        return inspections;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Inspections findById(Integer inspectionsId) {
        LOGGER.debug("Finding Inspections by id: {}", inspectionsId);
        return this.wmGenericDao.findById(inspectionsId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public Inspections update(Inspections inspections) throws EntityNotFoundException {
        LOGGER.debug("Updating Inspections with information: {}", inspections);
        this.wmGenericDao.update(inspections);

        Integer inspectionsId = inspections.getId();

        return this.wmGenericDao.findById(inspectionsId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public Inspections delete(Integer inspectionsId) throws EntityNotFoundException {
        LOGGER.debug("Deleting Inspections with id: {}", inspectionsId);
        Inspections deleted = this.wmGenericDao.findById(inspectionsId);
        if (deleted == null) {
            LOGGER.debug("No Inspections found with id: {}", inspectionsId);
            throw new EntityNotFoundException(String.valueOf(inspectionsId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<Inspections> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Inspections");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<Inspections> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Inspections");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table Inspections to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }



}

