import com.civicxpress.cx2.MasterCases;


/**
 * ServiceImpl object for domain model class MasterCases.
 *
 * @see MasterCases
 */
@Service("cx2.MasterCasesService")
public class MasterCasesServiceImpl implements MasterCasesService {

    private static final Logger LOGGER = LoggerFactory.getLogger(MasterCasesServiceImpl.class);


    @Autowired
    @Qualifier("cx2.MasterCasesDao")
    private WMGenericDao<MasterCases, String> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<MasterCases, String> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public MasterCases create(MasterCases masterCases) {
        LOGGER.debug("Creating a new MasterCases with information: {}", masterCases);
        MasterCases masterCasesCreated = this.wmGenericDao.create(masterCases);
        return masterCasesCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public MasterCases getById(String mastercasesId) throws EntityNotFoundException {
        LOGGER.debug("Finding MasterCases by id: {}", mastercasesId);
        MasterCases masterCases = this.wmGenericDao.findById(mastercasesId);
        if (masterCases == null){
            LOGGER.debug("No MasterCases found with id: {}", mastercasesId);
            throw new EntityNotFoundException(String.valueOf(mastercasesId));
        }
        return masterCases;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public MasterCases findById(String mastercasesId) {
        LOGGER.debug("Finding MasterCases by id: {}", mastercasesId);
        return this.wmGenericDao.findById(mastercasesId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public MasterCases update(MasterCases masterCases) throws EntityNotFoundException {
        LOGGER.debug("Updating MasterCases with information: {}", masterCases);
        this.wmGenericDao.update(masterCases);

        String mastercasesId = masterCases.getCaseGuid();

        return this.wmGenericDao.findById(mastercasesId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public MasterCases delete(String mastercasesId) throws EntityNotFoundException {
        LOGGER.debug("Deleting MasterCases with id: {}", mastercasesId);
        MasterCases deleted = this.wmGenericDao.findById(mastercasesId);
        if (deleted == null) {
            LOGGER.debug("No MasterCases found with id: {}", mastercasesId);
            throw new EntityNotFoundException(String.valueOf(mastercasesId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<MasterCases> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all MasterCases");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<MasterCases> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all MasterCases");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table MasterCases to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}

