import com.civicxpress.cx2.OutcomeFee;


/**
 * ServiceImpl object for domain model class OutcomeFee.
 *
 * @see OutcomeFee
 */
@Service("cx2.OutcomeFeeService")
public class OutcomeFeeServiceImpl implements OutcomeFeeService {

    private static final Logger LOGGER = LoggerFactory.getLogger(OutcomeFeeServiceImpl.class);


    @Autowired
    @Qualifier("cx2.OutcomeFeeDao")
    private WMGenericDao<OutcomeFee, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<OutcomeFee, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public OutcomeFee create(OutcomeFee outcomeFee) {
        LOGGER.debug("Creating a new OutcomeFee with information: {}", outcomeFee);
        OutcomeFee outcomeFeeCreated = this.wmGenericDao.create(outcomeFee);
        return outcomeFeeCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public OutcomeFee getById(Integer outcomefeeId) throws EntityNotFoundException {
        LOGGER.debug("Finding OutcomeFee by id: {}", outcomefeeId);
        OutcomeFee outcomeFee = this.wmGenericDao.findById(outcomefeeId);
        if (outcomeFee == null){
            LOGGER.debug("No OutcomeFee found with id: {}", outcomefeeId);
            throw new EntityNotFoundException(String.valueOf(outcomefeeId));
        }
        return outcomeFee;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public OutcomeFee findById(Integer outcomefeeId) {
        LOGGER.debug("Finding OutcomeFee by id: {}", outcomefeeId);
        return this.wmGenericDao.findById(outcomefeeId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public OutcomeFee update(OutcomeFee outcomeFee) throws EntityNotFoundException {
        LOGGER.debug("Updating OutcomeFee with information: {}", outcomeFee);
        this.wmGenericDao.update(outcomeFee);

        Integer outcomefeeId = outcomeFee.getId();

        return this.wmGenericDao.findById(outcomefeeId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public OutcomeFee delete(Integer outcomefeeId) throws EntityNotFoundException {
        LOGGER.debug("Deleting OutcomeFee with id: {}", outcomefeeId);
        OutcomeFee deleted = this.wmGenericDao.findById(outcomefeeId);
        if (deleted == null) {
            LOGGER.debug("No OutcomeFee found with id: {}", outcomefeeId);
            throw new EntityNotFoundException(String.valueOf(outcomefeeId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<OutcomeFee> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all OutcomeFees");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<OutcomeFee> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all OutcomeFees");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table OutcomeFee to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }



}

