import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.Pudapplication;


/**
 * ServiceImpl object for domain model class Pudapplication.
 *
 * @see Pudapplication
 */
@Service("cx2.PudapplicationService")
public class PudapplicationServiceImpl implements PudapplicationService {

    private static final Logger LOGGER = LoggerFactory.getLogger(PudapplicationServiceImpl.class);


    @Autowired
    @Qualifier("cx2.PudapplicationDao")
    private WMGenericDao<Pudapplication, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Pudapplication, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public Pudapplication create(Pudapplication pudapplication) {
        LOGGER.debug("Creating a new Pudapplication with information: {}", pudapplication);
        Pudapplication pudapplicationCreated = this.wmGenericDao.create(pudapplication);
        return pudapplicationCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Pudapplication getById(Integer pudapplicationId) throws EntityNotFoundException {
        LOGGER.debug("Finding Pudapplication by id: {}", pudapplicationId);
        Pudapplication pudapplication = this.wmGenericDao.findById(pudapplicationId);
        if (pudapplication == null){
            LOGGER.debug("No Pudapplication found with id: {}", pudapplicationId);
            throw new EntityNotFoundException(String.valueOf(pudapplicationId));
        }
        return pudapplication;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Pudapplication findById(Integer pudapplicationId) {
        LOGGER.debug("Finding Pudapplication by id: {}", pudapplicationId);
        return this.wmGenericDao.findById(pudapplicationId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public Pudapplication update(Pudapplication pudapplication) throws EntityNotFoundException {
        LOGGER.debug("Updating Pudapplication with information: {}", pudapplication);
        this.wmGenericDao.update(pudapplication);

        Integer pudapplicationId = pudapplication.getId();

        return this.wmGenericDao.findById(pudapplicationId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public Pudapplication delete(Integer pudapplicationId) throws EntityNotFoundException {
        LOGGER.debug("Deleting Pudapplication with id: {}", pudapplicationId);
        Pudapplication deleted = this.wmGenericDao.findById(pudapplicationId);
        if (deleted == null) {
            LOGGER.debug("No Pudapplication found with id: {}", pudapplicationId);
            throw new EntityNotFoundException(String.valueOf(pudapplicationId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<Pudapplication> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Pudapplications");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<Pudapplication> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Pudapplications");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table Pudapplication to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }



}

