package com.civicxpress.cx2.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/

import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.data.model.AggregationInfo;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.StandardViews;
import com.civicxpress.cx2.UserViewPreferences;


/**
 * ServiceImpl object for domain model class StandardViews.
 *
 * @see StandardViews
 */
@Service("cx2.StandardViewsService")
public class StandardViewsServiceImpl implements StandardViewsService {

    private static final Logger LOGGER = LoggerFactory.getLogger(StandardViewsServiceImpl.class);

    @Autowired
	@Qualifier("cx2.UserViewPreferencesService")
	private UserViewPreferencesService userViewPreferencesService;

    @Autowired
    @Qualifier("cx2.StandardViewsDao")
    private WMGenericDao<StandardViews, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<StandardViews, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public StandardViews create(StandardViews standardViews) {
        LOGGER.debug("Creating a new StandardViews with information: {}", standardViews);
        StandardViews standardViewsCreated = this.wmGenericDao.create(standardViews);
        if(standardViewsCreated.getUserViewPreferenceses() != null) {
            for(UserViewPreferences userViewPreferencese : standardViewsCreated.getUserViewPreferenceses()) {
                userViewPreferencese.setStandardViews(standardViewsCreated);
                LOGGER.debug("Creating a new child UserViewPreferences with information: {}", userViewPreferencese);
                userViewPreferencesService.create(userViewPreferencese);
            }
        }
        return standardViewsCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public StandardViews getById(Integer standardviewsId) throws EntityNotFoundException {
        LOGGER.debug("Finding StandardViews by id: {}", standardviewsId);
        StandardViews standardViews = this.wmGenericDao.findById(standardviewsId);
        if (standardViews == null){
            LOGGER.debug("No StandardViews found with id: {}", standardviewsId);
            throw new EntityNotFoundException(String.valueOf(standardviewsId));
        }
        return standardViews;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public StandardViews findById(Integer standardviewsId) {
        LOGGER.debug("Finding StandardViews by id: {}", standardviewsId);
        return this.wmGenericDao.findById(standardviewsId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public StandardViews update(StandardViews standardViews) throws EntityNotFoundException {
        LOGGER.debug("Updating StandardViews with information: {}", standardViews);
        this.wmGenericDao.update(standardViews);

        Integer standardviewsId = standardViews.getId();

        return this.wmGenericDao.findById(standardviewsId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public StandardViews delete(Integer standardviewsId) throws EntityNotFoundException {
        LOGGER.debug("Deleting StandardViews with id: {}", standardviewsId);
        StandardViews deleted = this.wmGenericDao.findById(standardviewsId);
        if (deleted == null) {
            LOGGER.debug("No StandardViews found with id: {}", standardviewsId);
            throw new EntityNotFoundException(String.valueOf(standardviewsId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<StandardViews> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all StandardViews");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<StandardViews> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all StandardViews");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table StandardViews to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<UserViewPreferences> findAssociatedUserViewPreferenceses(Integer id, Pageable pageable) {
        LOGGER.debug("Fetching all associated userViewPreferenceses");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("standardViews.id = '" + id + "'");

        return userViewPreferencesService.findAll(queryBuilder.toString(), pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service UserViewPreferencesService instance
	 */
	protected void setUserViewPreferencesService(UserViewPreferencesService service) {
        this.userViewPreferencesService = service;
    }

}

