import java.util.HashMap;
import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.VendorApprovals;


/**
 * ServiceImpl object for domain model class VendorApprovals.
 *
 * @see VendorApprovals
 */
@Service("cx2.VendorApprovalsService")
public class VendorApprovalsServiceImpl implements VendorApprovalsService {

    private static final Logger LOGGER = LoggerFactory.getLogger(VendorApprovalsServiceImpl.class);


    @Autowired
    @Qualifier("cx2.VendorApprovalsDao")
    private WMGenericDao<VendorApprovals, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<VendorApprovals, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public VendorApprovals create(VendorApprovals vendorApprovals) {
        LOGGER.debug("Creating a new VendorApprovals with information: {}", vendorApprovals);
        VendorApprovals vendorApprovalsCreated = this.wmGenericDao.create(vendorApprovals);
        return vendorApprovalsCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public VendorApprovals getById(Integer vendorapprovalsId) throws EntityNotFoundException {
        LOGGER.debug("Finding VendorApprovals by id: {}", vendorapprovalsId);
        VendorApprovals vendorApprovals = this.wmGenericDao.findById(vendorapprovalsId);
        if (vendorApprovals == null){
            LOGGER.debug("No VendorApprovals found with id: {}", vendorapprovalsId);
            throw new EntityNotFoundException(String.valueOf(vendorapprovalsId));
        }
        return vendorApprovals;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public VendorApprovals findById(Integer vendorapprovalsId) {
        LOGGER.debug("Finding VendorApprovals by id: {}", vendorapprovalsId);
        return this.wmGenericDao.findById(vendorapprovalsId);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public VendorApprovals getByMunicipalityIdAndVendorId(Integer municipalityId, Integer vendorId) {
        Map<String, Object> municipalityIdAndVendorIdMap = new HashMap<>();
        municipalityIdAndVendorIdMap.put("municipalityId", municipalityId);
        municipalityIdAndVendorIdMap.put("vendorId", vendorId);

        LOGGER.debug("Finding VendorApprovals by unique keys: {}", municipalityIdAndVendorIdMap);
        VendorApprovals vendorApprovals = this.wmGenericDao.findByUniqueKey(municipalityIdAndVendorIdMap);

        if (vendorApprovals == null){
            LOGGER.debug("No VendorApprovals found with given unique key values: {}", municipalityIdAndVendorIdMap);
            throw new EntityNotFoundException(String.valueOf(municipalityIdAndVendorIdMap));
        }

        return vendorApprovals;
    }

	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public VendorApprovals update(VendorApprovals vendorApprovals) throws EntityNotFoundException {
        LOGGER.debug("Updating VendorApprovals with information: {}", vendorApprovals);
        this.wmGenericDao.update(vendorApprovals);

        Integer vendorapprovalsId = vendorApprovals.getId();

        return this.wmGenericDao.findById(vendorapprovalsId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public VendorApprovals delete(Integer vendorapprovalsId) throws EntityNotFoundException {
        LOGGER.debug("Deleting VendorApprovals with id: {}", vendorapprovalsId);
        VendorApprovals deleted = this.wmGenericDao.findById(vendorapprovalsId);
        if (deleted == null) {
            LOGGER.debug("No VendorApprovals found with id: {}", vendorapprovalsId);
            throw new EntityNotFoundException(String.valueOf(vendorapprovalsId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<VendorApprovals> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all VendorApprovals");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<VendorApprovals> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all VendorApprovals");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table VendorApprovals to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }



}

