package com.civicxpress.cx2.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/


import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;

import com.civicxpress.cx2.Violations;


/**
 * ServiceImpl object for domain model class Violations.
 *
 * @see Violations
 */
@Service("cx2.ViolationsService")
public class ViolationsServiceImpl implements ViolationsService {

    private static final Logger LOGGER = LoggerFactory.getLogger(ViolationsServiceImpl.class);


    @Autowired
    @Qualifier("cx2.ViolationsDao")
    private WMGenericDao<Violations, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Violations, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "cx2TransactionManager")
    @Override
	public Violations create(Violations violations) {
        LOGGER.debug("Creating a new Violations with information: {}", violations);
        Violations violationsCreated = this.wmGenericDao.create(violations);
        return violationsCreated;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Violations getById(Integer violationsId) throws EntityNotFoundException {
        LOGGER.debug("Finding Violations by id: {}", violationsId);
        Violations violations = this.wmGenericDao.findById(violationsId);
        if (violations == null){
            LOGGER.debug("No Violations found with id: {}", violationsId);
            throw new EntityNotFoundException(String.valueOf(violationsId));
        }
        return violations;
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Violations findById(Integer violationsId) {
        LOGGER.debug("Finding Violations by id: {}", violationsId);
        return this.wmGenericDao.findById(violationsId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "cx2TransactionManager")
	@Override
	public Violations update(Violations violations) throws EntityNotFoundException {
        LOGGER.debug("Updating Violations with information: {}", violations);
        this.wmGenericDao.update(violations);

        Integer violationsId = violations.getViolationId();

        return this.wmGenericDao.findById(violationsId);
    }

    @Transactional(value = "cx2TransactionManager")
	@Override
	public Violations delete(Integer violationsId) throws EntityNotFoundException {
        LOGGER.debug("Deleting Violations with id: {}", violationsId);
        Violations deleted = this.wmGenericDao.findById(violationsId);
        if (deleted == null) {
            LOGGER.debug("No Violations found with id: {}", violationsId);
            throw new EntityNotFoundException(String.valueOf(violationsId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public Page<Violations> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Violations");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Page<Violations> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Violations");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "cx2TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service cx2 for table Violations to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "cx2TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }



}

